'use strict'
// const http = require('http');
// const restify = require('../');

const jsonClient = require("restify-clients").createJsonClient(
    "https://registry.us-west-2.streamingtest.titus.netflix.net:7002"
);

const version = '9.32.0-shabdbc1fc';
const restifyOpts = {
    headers: {
        // accept: "application/vnd.docker.distribution.manifest.v2+json"
        accept: "application/vnd.docker.distribution.manifest.list.v2+json"
    },
    retry: false,
    path: `/v2/uiplatform.nodequark/manifests/${version}`
};

jsonClient.get(
    restifyOpts,
    function(err, _, res) {
        console.log(res);
        if (err) {
            console.log('request errored')
        } else {
            console.log('request success')
        }
    }
);

